# Add all Q&A pairs
created_count = 0
updated_count = 0

for qa in qa_dataset:
    obj, created = ChatbotTraining.objects.update_or_create(
        question=qa['question'],
        defaults={
            'answer': qa['answer'],
            'intent': qa['intent'],
            'keywords': qa['keywords'],
            'is_active': True,
            'created_by': admin_user
        }
    )
    
    if created:
        created_count += 1
    else:
        updated_count += 1

# Get total count
total = ChatbotTraining.objects.filter(is_active=True).count()

